
public class NameAndInitials {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String firstName;
		String lastName;
		char firstInitial;
		char lastInitial;
		
		firstName = "Daniel";
		lastName = "Schonfeld";
		firstInitial = 'D';
		lastInitial = 'S';
		
		System.out.println("Full Name: " + firstName + " " + lastName);
		System.out.println("Initials: " + firstInitial + "." + lastInitial + ".");
	}

}
